    private void greetButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_greetButtonActionPerformed
        javax.swing.JOptionPane.showMessageDialog(this, "Hello!");
    }//GEN-LAST:event_greetButtonActionPerformed

    private void stopButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_stopButtonActionPerformed
        stopButton.setEnabled(false); 
        startButton.setEnabled(true);
        greetButton.setEnabled(false);
    }//GEN-LAST:event_stopButtonActionPerformed

    private void startButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_startButtonActionPerformed
        startButton.setEnabled(false);
        stopButton.setEnabled(true);
        greetButton.setEnabled(true);
    }//GEN-LAST:event_startButtonActionPerformed

