    public void realTimePriceDisplayTextFieldUpdate() {
        QuantityInputTextField.getDocument().addDocumentListener(new javax.swing.event.DocumentListener() {
            
            @Override
            public void insertUpdate(javax.swing.event.DocumentEvent e) {
                updatePrice();
            }

            @Override
            public void removeUpdate(javax.swing.event.DocumentEvent e) {
                updatePrice();
            }

            @Override
            public void changedUpdate(javax.swing.event.DocumentEvent e) {
                updatePrice();
            }

            private void updatePrice() {
                String qty = getQuantityInputTextField().replace(",", "");
                if (qty.isEmpty()) {
                    setPriceDisplayTextField("");
                    return;
                }

                int totalPrice= getPrice() * Integer.parseInt(qty);
                String formatted = new java.text.DecimalFormat("#,###").format(totalPrice);
                setPriceDisplayTextField(formatted);
            }
        });
    }
    
    public void resetComponent() {
        MenuComboBox.setSelectedIndex(0);
        setPrice(0);
        setImageMenuLabel("/resource/images/200x200px/original-ck.jpg");
         setImageOptionLabel("/resource/images/200x200px/ck-thinking.png");
        setPriceDisplayTextField("");
        setQuantityInputTextField("");
        setEnableQuantityInputTextField(false);
        DineinCheckBox.setSelected(false);
        TakeAwayCheckBox.setSelected(false);
    }
    
