    private void setPrice(int price) {
        this.price = price;
    }
    
    private void setEnableOrderButton(boolean status) {
        OrderButton.setEnabled(status);
    }
    
    private void setEnableCheckBoxComponent(boolean status) {
        DineinCheckBox.setEnabled(status);
        TakeAwayCheckBox.setEnabled(status);
        
        if(DineinCheckBox.isSelected()) {
            TakeAwayCheckBox.setEnabled(false);
            setImageOptionLabel("/resource/images/200x200px/ck-fastwork.png");
            setEnableOrderButton(status);
        } 
        else if (TakeAwayCheckBox.isSelected()) {
            DineinCheckBox.setEnabled(false);
            setImageOptionLabel("/resource/images/200x200px/ck-fastdrive.png");
            setEnableOrderButton(status);
        }
        else {
            setImageOptionLabel("/resource/images/200x200px/ck-thinking.png");
            setEnableOrderButton(false);
        }
    }
    
    private void setEnableQuantityInputTextField(boolean status) {
        QuantityInputTextField.setEnabled(status);
        QuantityInputTextField.setEditable(status );
    }
    
