    private void OrderButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_OrderButtonActionPerformed
        if(!getQuantityInputTextField().isEmpty()) { 
            OrderDetailDialog odd = new OrderDetailDialog(this);
            odd.setLocationRelativeTo(this);
            odd.setVisible(true);
            return;
        }
        javax.swing.JOptionPane.showMessageDialog(this, "Please enter quantity!!" , "Input Error", javax.swing.JOptionPane.ERROR_MESSAGE);
    }//GEN-LAST:event_OrderButtonActionPerformed

    private void MenuComboBoxActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_MenuComboBoxActionPerformed
        switch(getMenuComboBox()) {
            case "Chicken Drumstick" :
                setPrice(45);
                setImageMenuLabel("/resource/images/200x200px/ck-chicken-drumstick.png");
                setPriceDisplayTextField("45");
                setQuantityInputTextField("1");
                setEnableQuantityInputTextField(true);
                setEnableCheckBoxComponent(true);
                break;
            case "Cola" :
                setPrice(20);
                setImageMenuLabel("/resource/images/200x200px/ck-cola.png");
                setPriceDisplayTextField("20");
                setQuantityInputTextField("1");
                setEnableQuantityInputTextField(true);
                setEnableCheckBoxComponent(true);
                break;
            case "French Fries" :
                setPrice(39);
                setImageMenuLabel("/resource/images/200x200px/ck-french-fries.png");
                setPriceDisplayTextField("39");
                setQuantityInputTextField("1");
                setEnableQuantityInputTextField(true);
                setEnableCheckBoxComponent(true);
                break;
            case "Hamburger" :
                setPrice(69);
                setImageMenuLabel("/resource/images/200x200px/ck-hamburger.png");
                setPriceDisplayTextField("69");
                setQuantityInputTextField("1");
                setEnableQuantityInputTextField(true);
                setEnableCheckBoxComponent(true);
                break;
            case "Ice Cream" :
                setPrice(25);
                setImageMenuLabel("/resource/images/200x200px/ck-ice-cream.png");
                setPriceDisplayTextField("25");
                setQuantityInputTextField("1");
                setEnableQuantityInputTextField(true);
                setEnableCheckBoxComponent(true);
                break;
            default :
                resetComponent();
                setEnableCheckBoxComponent(false);
        }
    }//GEN-LAST:event_MenuComboBoxActionPerformed

    private void QuantityInputTextFieldKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_QuantityInputTextFieldKeyTyped
        char val = evt.getKeyChar();
        if (!Character.isDigit(val)) {
            evt.consume();
        }
    }//GEN-LAST:event_QuantityInputTextFieldKeyTyped

    private void QuantityInputTextFieldKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_QuantityInputTextFieldKeyReleased
        String text = getQuantityInputTextField().replace(",", "");
        if (text.isEmpty()) return;
        setQuantityInputTextField(
                new java.text.DecimalFormat("#,###").format(Integer.parseInt(text))
        );    
    }//GEN-LAST:event_QuantityInputTextFieldKeyReleased

