    private SoundController  soundController;
    private int score = 0;
    
    public String getAnswerTextField() {
        return answerTextField.getText();
    }
    
    public int getCorrectAnswer() {
       String expression = getQuestionLabel()
               .replace("\u0078", "\u002A")
               .replace("\u00F7", "\u002F")
               .replaceAll("[\u2713\u2718,]", "")
               .trim();
       net.objecthunter.exp4j.Expression e = new net.objecthunter.exp4j.ExpressionBuilder(expression).build();
        return (int) e.evaluate();
    }
    
    public String getQuestionLabel() {
        return questionLabel.getText();
    }
    
    private void setAnswerTextField(String text) {
        answerTextField.setText(text);
    }

    private void setQuestionLabel(String question) {
        questionLabel.setText(question);
    }

    private void setScoreLabel(String point) {
        scoreLabel.setText(point);
    }

    private void setScoreProgressBar(int val) {
        scoreProgressBar.setValue(val);
    }
    
    private void setCorrectAnswer(boolean isCorrect) {
        if(isCorrect) {
            playSound(0, 2000);
            answerButton.setEnabled(false);
            questionLabel.setForeground(java.awt.Color.GREEN);
            setQuestionLabel(numberFormat(Integer.toString(getCorrectAnswer())) + "\u2713");
            setScoreLabel(Integer.toString(++score));
            setScoreProgressBar(score);
            return;
        }
        playSound(1, 2000);
        answerButton.setEnabled(false);
        questionLabel.setForeground(java.awt.Color.RED);
        setQuestionLabel(numberFormat(Integer.toString(getCorrectAnswer())) + "\u2718");
    }
    
    private void resetComponent(boolean reset) {
        if(reset) {
            score = 0;
            setScoreLabel("0");
            setScoreProgressBar(score);        
        }
        answerButton.setEnabled(true);
        questionLabel.setForeground(java.awt.Color.BLACK);
        setQuestionLabel(creatRandomQuestion());
        setAnswerTextField("");
    }
    
    private void checkAnswer() {
        if(getAnswerTextField().isEmpty() || getAnswerTextField().equals("\u002D")){
            callOptionPane("Please enter your answer", "Answer Required", 2);
        }
        else {
            setCorrectAnswer(toInteger(getAnswerTextField()) == getCorrectAnswer());
        }
    }
    
    public String creatRandomQuestion() {
        java.util.Random random = new java.util.Random(); 
        StringBuilder expression = new StringBuilder();
        int operatorQty = random.nextInt(4); 
        char[] operators = { 
            '\u002B', // (+)
            '\u002D', // (-)
            '\u0078',  // (x)
            '\u00F7'  // (รท)
        };
        expression.append(random.nextInt(100) + 1);
        
        for (int i = 0; i <= operatorQty; i++) {
            expression.append(" ");
            expression.append(operators[random.nextInt(operators.length)]);
            expression.append(" ");
            expression.append(random.nextInt(100) + 1);
        }
        return expression.toString();
    }
    
    public void callOptionPane(String message, String title, int type) {
        javax.swing.JOptionPane.showMessageDialog(this,  message, title, type);
    }
    
    public int toInteger(String val) {
        return Integer.parseInt(val.replace(",", ""));
    }
    
    private String numberFormat(String val) {
        try{
            if (!val.isEmpty() && val != null) {
                return new java.text.DecimalFormat("#,###").format(toInteger(val));
            }
        }
        catch (NumberFormatException e) {
            return val;
        }
        return "";
    }
    
    private void numberOnly(java.awt.event.KeyEvent evt) {
        char val = evt.getKeyChar();
        
        if (!Character.isDigit(val)) {
            if (val != '\u002D' || answerTextField.getCaretPosition() != 0) {
                evt.consume(); 
            } 
        }
    }
    
    private void playSound(int selectSound, int sleepTime) {
        soundController.play(selectSound);
        new Thread(() -> {
                try {
                    Thread.sleep(sleepTime);
                    if(score == scoreProgressBar.getMaximum()) {
                        soundController.play(2);
                        callOptionPane("Congratulations, you're genius!", "Congratulations", 1);
                        resetComponent(true);
                    }
                    resetComponent(false);
                } 
                catch (Exception e) {
                    callOptionPane("Error", e.toString(), 0);
                }
            }).start();
    }    
