    private void goButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_goButtonActionPerformed
        if(!getStudentID().isEmpty()) {
            new Database(this, 'a');
            return;
        }
        callOptionPane("Please enter the student_id", "Error", 0);
    }//GEN-LAST:event_goButtonActionPerformed

    private void exitButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_exitButtonActionPerformed
        System.exit(0);
    }//GEN-LAST:event_exitButtonActionPerformed

    private void fNameTextFieldKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_fNameTextFieldKeyTyped
        if(getFirstName().length() == 50) {
            evt.consume();
        }
        characterOnly(evt);
    }//GEN-LAST:event_fNameTextFieldKeyTyped

    private void stdIDTextFieldKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_stdIDTextFieldKeyTyped
        if(getStudentID().length() == 20) {
            evt.consume();
        }
        numberOnly(evt);
    }//GEN-LAST:event_stdIDTextFieldKeyTyped

    private void sNameTextFieldKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_sNameTextFieldKeyTyped
         if(getSurname().length() == 50) {
            evt.consume();
        }
        characterOnly(evt);
    }//GEN-LAST:event_sNameTextFieldKeyTyped

    private void showDataButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_showDataButtonActionPerformed
        new Database(this, 's');
    }//GEN-LAST:event_showDataButtonActionPerformed

