    private String getStudentID() {
        return stdIDTextField.getText();
    }
    
    private String getFirstName() {
        return fNameTextField.getText();
    }
    
    private String getSurname() {
        return sNameTextField.getText();
    }
    
    private int getScore() {
        return ((Number) scoreSpinner.getValue()).intValue();
    }
    
    private String gradeCalculate() {
        javax.swing.JSpinner[] spinners = {
            gradeASpinner,
            gradeBPSpinner,
            gradeBSpinner,
            gradeCPSpinner,
            gradeCSpinner,
            gradeDPSpinner,
            gradeDSpinner,
            gradeFSpinner
        };
        
        for(javax.swing.JSpinner sp : spinners) {
            String grade = ((javax.swing.border.TitledBorder) sp.getBorder()).getTitle();
            float rate = ((Number) sp.getValue()).floatValue();
            
            if(getScore() >= rate || grade.contains("F")) {
                return grade;
            }
        }
        return null;
    }
        
    protected String[] studentData() {
        return new String[] {getStudentID(), getFirstName(), getSurname(), gradeCalculate()};
    }
    
    public void callOptionPane(String message, String title, int type) {
        javax.swing.JOptionPane.showMessageDialog(this,  message, title, type);
    }
    
    private void numberOnly(java.awt.event.KeyEvent evt) {
        char val = evt.getKeyChar();

        if (!Character.isDigit(val)) {
            evt.consume();
        }
    }
    
    private void characterOnly(java.awt.event.KeyEvent evt) {
        char val = evt.getKeyChar();
        if (String.valueOf(val).matches("[^a-zA-Z]")) {
            evt.consume();
        }
    }
